@media screen and (min-width: 49em) {
  .grid {
    display: grid;
    grid-template-columns: 1fr 1fr 1fr 1fr;
    grid-template-rows: auto;
    grid-template-areas: 
      "nav nav nav nav " 
      "header header header header " 
      " chart chart chart chart"
      " problem problem faq faq "
      " service service adv adv "
      " comment comment about about";
    grid-gap: 1em;
  }

  .nav {
    grid-area: nav;
  }
  .header {
    grid-area: header;
  }
  .faq {
    grid-area: faq;
  }
  .about {
    grid-area: about;
  }
  .advertisement {
    grid-area: adv;
  }
  .reportProblem {
    grid-area: problem;
  }
  .downChart{
    grid-area: chart;
  }
  .serviceDetails{
    grid-area: service;
  }
  .commentsSection{
    grid-area: comment;
  }

}

