  .grid {
    display: grid;
    grid-template-columns: 1fr 1fr 1fr 1fr 100px 100px 100px;
    grid-template-rows: auto;
    grid-template-areas: 
      "nav nav nav nav nav nav nav" 
      "header header header header header header header" 
      " chart chart chart chart problem problem problem"
      " chart chart chart chart service service service"
      " faq faq faq faq adv adv adv"
      " comment comment comment comment about about about"
      " . . . . about about about";
    grid-gap: 1em;
  }
}

@media screen and (min-width: 72em) {
  .grid {
    display: grid;
    grid-template-columns: 1fr 1fr 1fr 1fr 1fr 1fr 100px 100px 100px;
    grid-template-rows: auto;
    grid-template-areas: 
      "nav nav nav nav nav nav nav nav nav" 
      "header header header header header header header header header" 
      "chart chart chart chart chart chart problem problem problem"
      "chart chart chart chart chart chart service service service"
      "comment comment comment faq faq faq adv adv adv"
      "comment comment comment about about about . . ."
      ". . . about about about . . . ";
    grid-gap: 1em;
  }
}

@media screen and (min-width: 93em) {
  .grid {
    display: grid;
    grid-template-columns: 10% 1fr 1fr 1fr 1fr 1fr 1fr 100px 100px 100px 10%;
    grid-template-rows: auto;
    grid-template-areas: 
      "nav nav nav nav nav nav nav nav nav nav nav" 
      ". header header header header header header header header header ." 
      ". chart chart chart chart chart chart problem problem problem ."
      ". chart chart chart chart chart chart service service service ."
      ". chart chart chart chart chart chart . . . ."
      ". comment comment comment faq faq faq adv adv adv ."
      ". comment comment comment about about about . . . ."
      ". . . . about about about . . . . ";
    grid-gap: 1em;
  }
}
